Node.java   
/**
 * 栈节点
 */
public class Node
{
    /**数据*/
    public Object data;
    /**下一个节点*/
    public Node next ;

    public Node(Object data ,Node next)
    {
        this.data=data ;
        this.next =next;
    }
}

MyStack.java
public class MyStack
{
    /**栈顶节点*/
    private Node topNode ;
    /**栈大小*/
    public int stackSize;

    /**
     * 入栈
     */
    public void push(Object data)
    {
        //新节点
        Node newNode = new Node(data, topNode);
        //对外公布 新栈顶元素
        topNode= newNode ;
        stackSize++ ;
    }

    /**
     * 出栈
     */
    public void pop()
    {
        //需要出栈的节点
        Node temp= topNode ;
        //对外公布  新的栈顶元素
        topNode = temp.next ;
        stackSize-- ;

    }

    /**打印该栈所有元素*/
    public void showStackInfo()
    {
        Node temp =topNode ;
        if(temp==null)
        {
            System.out.println("空栈");
            return ;
        }

        while(temp !=null )
        {
            System.out.println("栈节点值="+ temp.data);
            temp = temp.next;
        }
        System.out.println("---------end-------");
    }
}

测试
public static void main(String[] args)
{
  MyStack myStack = new MyStack();
  myStack.showStackInfo();  
  myStack.push(1) ;
  myStack.push(2) ;
  myStack.showStackInfo();
  myStack.pop();
  myStack.showStackInfo();
}


