    /**
     * 停止绘制
     */
    public void stopView( ) {
        mRecDataList.clear();
        mBackCanVans.drawColor(Color.TRANSPARENT, PorterDuff.Mode.CLEAR);
    }

    public void updateDataList(int dbValue ){
        if(mRecDataList.size() > listMaxSize){
            mRecDataList.add(dbValue) ;
            mRecDataList.remove(0) ;
        }else{
